sys_script_email.script:
(function runMailScript(current, template, email, email_action, event) {

	gs.info("Email address:"+ current.email);
	// Add your code here
	template.print("Your BeyondMove account have been granted  Shipper Role. Start Shipping!!");
	email.addAddress("to", current.email+'',current.user_name+'');
	gs.info("Email sent to :"+ current.email);

})(current, template, email, email_action, event);
